export const initialState = {
    isLoggedIn: false,
    user:{},
};

export const LOG_IN = 'LOG_IN'; //액션의 이름
export const LOG_OUT = 'LOG_OUT'; //액션의 이름

const loginAction = {
    type: LOG_IN,
    data: {
        nickname: '디와이',
    },
};
const logoutAction={
  type: LOG_OUT,
};

const reducer = (state = initialState, action ) => {
    switch (action.type) {
        case loginAction:{
            return {
                ...state,
                isLoggedIn: true,
                user: action.data,
            }
        }

        case logoutAction:{
            return {
                ...state,
                isLoggedIn: false,
                user: null
            }
        }

        default:{
            return {
                ...state,
            }
        }
    }
};

export default reducer;
